import { Component } from '@angular/core';

@Component({
  selector: 'app-calc',
  templateUrl: './calc.component.html',
  styleUrls: ['./calc.component.scss']
})
export class CalcComponent {
  num1: number = 0;
  num2: number = 0;
  result: number = 0;
  input: string = "0";
  action: string = ""
  onClicCl(): void{
    this.input = "0";
    this.num1 =0;
    this.num2 =0;
    this.result =0;
  }
  onClicSum(value: string): void{
    this.num1 = Number(this.input);
    this.action = value;
    if (this.action!=null) {
      this.Actions();

    }
  }

  onClicNum(value: string):void{
    if (this.input=="0") {
      this.input=value;
    }else
     this.input=this.input+value;
  }

Actions(): void{
 switch(this.action){
  case '+':{
    this.result=this.num1+this.num2;
    break;
  }
  case '-':{
    this.result=this.num1-this.num2;
    break;
  }
  case '*':{
    this.result=this.num1*this.num2;
    break;
  }
  case '/':{
    this.result=this.num1/this.num2;
    break;
  }  
 }
}
}
<div class="border border-primary" style="height: 420px; width: 320px;">
    <h1 class="text-end border border-primary">{{input}}</h1>
    <div class="container text-center">
        <div class="row row-cols-2 row-cols-lg-4 g-2 g-lg-3 ">
          <div class="col ">
            <div  class="p-3"><button (click)="onClicNum('1')" type="button" class="btn btn-success btn-lg">1</button></div>
          </div>
          <div class="col">
            <div class="p-3"><button (click)="onClicNum('2')" type="button" class="btn btn-success btn-lg">2</button></div>
          </div>
          <div class="col">
            <div class="p-3"><button (click)="onClicNum('3')" type="button" class="btn btn-success btn-lg">3</button></div>
          </div>
          <div class="col">
            <div class="p-3"><button type="button" class="btn btn-primary btn-lg">+</button></div>
          </div>
          <div class="col">
            <div class="p-3"><button (click)="onClicNum('4')" type="button" class="btn btn-success btn-lg">4</button></div>
          </div>
          <div class="col">
            <div class="p-3"><button (click)="onClicNum('5')" type="button" class="btn btn-success btn-lg">5</button></div>
          </div>
          <div class="col">
            <div class="p-3"><button (click)="onClicNum('6')" type="button" class="btn btn-success btn-lg">6</button></div>
          </div>
          <div class="col">
            <div class="p-3"><button type="button" class="btn btn-primary btn-lg">-</button></div>
          </div>
          <div class="col">
            <div class="p-3"><button (click)="onClicNum('7')" type="button" class="btn btn-success btn-lg">7</button></div>
          </div>
          <div class="col">
            <div class="p-3"><button (click)="onClicNum('8')" type="button" class="btn btn-success btn-lg">8</button></div>
          </div>
          <div class="col">
            <div class="p-3"><button (click)="onClicNum('9')" type="button" class="btn btn-success btn-lg">9</button></div>
          </div>
          <div class="col">
            <div class="p-3"><button type="button" class="btn btn-primary btn-lg">*</button></div>
          </div>
          <div class="col">
            <div class="p-3"><button (click)="onClicCl()" type="button" class="btn btn-danger btn-lg">cl</button></div>
          </div>
          <div class="col">
            <div class="p-3"><button (click)="onClicNum('0')" type="button" class="btn btn-success btn-lg">0</button></div>
          </div>
          <div class="col">
            <div class="p-3"><button type="button" class="btn btn-info btn-lg">=</button></div>
          </div>
          <div class="col">
            <div class="p-3"><button type="button" class="btn btn-primary btn-lg">/</button></div>
          </div>

        </div>
      </div>
</div>
