import { Component } from '@angular/core';

@Component({
  selector: 'app-calc',
  templateUrl: './calc.component.html',
  styleUrls: ['./calc.component.scss']
})
export class CalcComponent {
  num1: number = 0;
  num2: number = 0;
  result: number = 0;
  input: string = "0";
  action: string = ""
  onClicCl(): void{
    this.input = "0";
    this.num1 =0;
    this.num2 =0;
    this.result =0;
  }
  onClicAction(value: string): void{
    this.num1 = Number(this.input);
    this.action = value;
    if (this.action!=null) {
      this.Actions();
      this.input = String(this.result);
    }
  }

  onClicNum(value: string):void{
    if (this.input=="0") {
      this.input=value;
    }else
     this.input=this.input+value;
  }

Actions(): void{
 switch(this.action){
  case '+':{
    this.result=this.num1+this.num2;
    break;
  }
  case '-':{
    this.result=this.num1-this.num2;
    break;
  }
  case '*':{
    this.result=this.num1*this.num2;
    break;
  }
  case '/':{
    this.result=this.num1/this.num2;
    break;
  }  
 }
}
}
