import { Component } from '@angular/core';

@Component({
  selector: 'app-calc',
  templateUrl: './calc.component.html',
  styleUrls: ['./calc.component.scss']
})
export class CalcComponent {
  num1: number = 0;
  num2: number = 0;
  result: number = 0;
  input: string = "0";




  onClicNum(value: string):void{
    if (this.input=="0") {
      this.input=value;
    }else
     this.input=this.input+value;
  }
}
