import { Component } from '@angular/core';

@Component({
  selector: 'app-calc',
  templateUrl: './calc.component.html',
  styleUrls: ['./calc.component.scss']
})
export class CalcComponent {
  num1: number = 0;
  num2: number = 0;
  result: number = 0;
  input: string = "";




  onClicNum1(value: string):void{
     this.input=this.input+value;
  }
}
